import todoListState from "../store/todoListState"

const saveTodo = () => {

    const {todos} = todoListState

    return(
        localStorage.setItem("todos", JSON.stringify(todos)),
        console.log(localStorage.getItem("todos"))
    )
}



export default saveTodo
