import React from "react";
import { Inputs } from "./components/inputs";
import { ColumnNames } from "./components/columnNames";
// import { observer } from "mobx-react-lite";
// import { CreateList } from "./components/todoList/index.jsx";
// import { ModalWindow } from "./components/modalWindow";
// import { todoListState } from "./store";
// import { SortingTodo } from "./components/sorting";
// import InputSearch from "./components/search";
// import saveTodo from "./helpers/localStorage";

export const TodoListApp = observer(() => {

    const { activeTask, todos } = todoListState;

    const [filtered, setFiltered] = React.useState([]);

    React.useEffect(() => {
        setFiltered(todos);
    }, [todos]);

    const search = (val) => {
        let currentTodos = [],
            newList = [];
        if (val !== "") {
            currentTodos = todos
            newList = currentTodos.filter((todo) => {
                const lc = todo.title.toLowerCase();
                const filter = val.toLowerCase();
                return lc.includes(filter);
            });
        } else {
            newList = todos;
        }
        setFiltered(newList);
    };

    return (
        <div className="container">
            <div className="listBlock">
                {/* <Inputs /> */}
                <div className="sortSearchBlock">
                    <div className="df">
                        {/* <InputSearch {...{ search }}/> */}
                        {/* <SortingTodo /> */}
                    </div>
                    {/* <ColumnNames /> */}
                </div>
                {/* <CreateList todos={filtered}/> */}
            </div>
            <div className="fullDescriptionBlock">
                {/* {activeTask && <ModalWindow activeTask={activeTask} />} */}
            </div>
        </div>
    );
});
