import { makeAutoObservable } from "mobx";

export class store {
    todos = {
       1: {id: "0", title: "123", description:"sfdklgfkds", }, 
    }
    activeTask = null;

    constructor() {
        makeAutoObservable(this);
    }

    addTodo(index) {
        this.todos[index];
    }

    setTodo(todo) {
        this.activeTask = todo;
    }

    removeTodo(id) {
       delete todos[id];
    }

    completeTodo(id) {
        this.todos = this.todos.map((todo) =>
            todo.id === id ? { ...todo, completed: !todo.completed } : todo
        );
    }
}
