import "./modalStyle.css";
import { observer } from "mobx-react-lite";
import todoListState from "../../store/todoListState";

export const ModalWindow = (title, ) => {
    return (
        <div className="modalWindowsBlock">
            <p>{todoListState.todos?.title}</p>
            <div>
                <p className="gray">description:</p>
                <p>{todoListState.todos?.description}</p>
            </div>
            <div>
                <p className="gray">status:</p>
                <p>{todoListState.todos.completed === true ? "done" : "not ready"}</p>
            </div>
            <button className="btnClose">Close</button>
        </div>
    );
};
