import { makeAutoObservable } from "mobx";

class TodoStore {
    todos = {};

    activeTodo = null;

    constructor() {
        makeAutoObservable(this);
    }

    addTodo(array) {
        this.todos[Object.keys(this.todos).length - 1 === -1 ? 0 : Object.values(this.todos)[Object.keys(this.todos).length - 1].id] = array;
    }

    setTodo(todo) {
        this.activeTodo = todo;
    }

    removeTodo(id) {
        delete this.todos[id];
        if (Object.keys(this.todos).length === 0) {
            this.todos = {}
        } 
    }

    // Подумать что сделать с чекбоксом

    completeTodo(TodoId, arrayIndex) {
        this.todos = Object.values(this.todos).map((todo) =>
            todo.id === TodoId ? console.log(todo[TodoId]. COP) : todo);
        // todo.completed ? true : false

    }
}



export const store = new TodoStore();
