import './sortingStyle'
import todoListState from '../../store/todoListState';

const SortingTodo = () => {

    const { todos } = todoListState

    const sorting = (arr) =>   {
        arr.sort(function (a, b) {
            if (a.name > b.name) {
              return 1;
            }
            if (a.name < b.name) {
              return -1;
            }

            return 0;
          });
    }  

    return (
        <button onClick={() => console.log(sorting(todos))}>A-Z</button>
    )
}

export default SortingTodo;
