import todoListState from "../store/todoListState"

const saveTodo = (arr) => {

    return(
        localStorage.setItem("todos", JSON.stringify(arr))
    )
}

localStorage.setItem("todos", JSON.stringify(todoListState.todos))

export default saveTodo
