import { makeAutoObservable, makeObservable } from "mobx";

class TodoList {
    todos = [
        {id:1, title:"title1", description: "Сходи в магазин", completed: false},
        {id:2, title:"title2", description: "Сходи выброси мусор сучка", completed: false},
        {id:3, title:"title1", description: "Сходи в магазин", completed: false},
        {id:4, title:"title1", description: "Сходи в магазин", completed: false},
        {id:2, title:"title2", description: "Сходи выброси мусор сучка", completed: false},
    ];

    constructor() {
        makeAutoObservable(this);
    }

    addTodo(todo) {
        this.todos.push(todo);
    }

    removeTodo(id) {
        this.todos = this.todos.filter(todo => todo.id !== id)
    }

    completeTodo(id) {
        this.todos = this.todos.map(todo => todo.id === id ? {...todo, completed: !todo.completed} : todo)
    }
}

export default new TodoList();
