import React from "react";
import todoListState from "../../store/todoListState";

const InputSearch = () => {
    const [value, setValue] = React.useState("");

    return (
        <div className="dflex">
            <input
            type='text'
            value={value}
            onChange={(event) => setValue(event.target.value)}
            ></input>
            <button onClick={() => (todoListState.searchTodo(value) , console.log(todoListState.searchArr, "|||", todoListState.todos))}>Search</button>
            {todoListState.searchArr.length !== 0 ? <button onClick={() => todoListState.searchArr = []}>look full todo</button> : null}
        </div>

    );
};

export default InputSearch;
