import React from "react";
import "./listStyle.css";
import { observer } from "mobx-react-lite";
import todoListState from "../../store/todoListState";
import { ModalWindow } from "../modalWindow/modal";

export const CreateList = observer(() => {
    return (
        <div className="todoListBlock">
            {todoListState.todos.map((todo) => (
                <>
                <div className="todoList" key={todo.id} >
                    <p className="id">#{todo.id}</p>
                    <p className="title">
                        {todo.title.length >= 7
                            ? todo.title.slice(0, 7) + "..."
                            : todo.title}
                    </p>
                    <p className="description">
                        {todo.description.length >= 7
                            ? todo.description.slice(0, 7) + "..."
                            : todo.description}
                    </p>
                    <input
                        type="checkbox"
                        checked={todo.completed}
                        onChange={() => todoListState.completeTodo(todo.id)}
                    />

                    {/* <button className="btnRemove" onClick={() => todoListState.removeTodo(id)}>X</button> */}
                </div>
                <ModalWindow title={todo?.title} description={todo?.description} completed={todo?.completed}/>
                </>

            ))}

        </div>
    );
});
