import './searchStyle.css'
import todoListState from '../../store/todoListState'
import InputSearch from './inputSearch'

const SearchTodosList = () => {

   const { searchArr } = todoListState

    return (
        searchArr.length !== '0' ? searchArr.map((todo) => (
            <div
                className="todo"
                key={todo.id}
                onClick={() => todoListState.setTodo(todo)}
            >
                <p className="id">#{todo.id}</p>
                <p className="title">
                    {todo.title.length >= 7
                        ? todo.title.slice(0, 7) + "..."
                        : todo.title}
                </p>
                <p className="description">
                    {todo.description.length >= 7
                        ? todo.description.slice(0, 7) + "..."
                        : todo.description}
                </p>
                <div className="inputBtnBlock">
                    <input
                        type="checkbox"
                        checked={todo.completed}
                        onChange={() => todoListState.completeTodo(todo.id)}
                    />
                    <button
                        onClick={() => todoListState.removeTodo(todo.id)}
                    >
                        x
                    </button>
                </div>
            </div>
        )) : ""
    )
}

export default SearchTodosList
