import React from "react";
import './style.css'
import { observer } from "mobx-react-lite";
import { store} from "../../store";
// import saveTodo from "../../helpers/localStorage";

export const TodoList = observer((props) => {
    const { todos, setTodo } = store;

    return (
        <ul className="todoListBlock">
            {Object.values(todos).map((todo) => (
                <li
                    className="todo"
                    key={todo.id}
                    onClick={() => store.setTodo(todo.id)}
                >
                    {console.log(todo.id)}
                    <p className="id">#{todo.id + 1}</p>
                    <p className="title">
                        {todo.title?.length >= 7
                            ? todo.title.slice(0, 7) + "..."
                            : todo.title}
                    </p>
                    <p className="description">
                        {todo.description?.length >= 7
                            ? todo.description.slice(0, 7) + "..."
                            : todo.description}
                    </p>
                    <div className="inputBtnBlock">
                        <input
                            type="checkbox"
                            checked={todo.completed}
                            onChange={() => store.completeTodo(todo.id)}
                        />
                        <button
                            onClick={() => (
                                store.removeTodo(todo.id)
                            )}
                        >
                            x
                        </button>
                    </div>
                </li>
            ))}
        </ul>
    );
});
