import { makeAutoObservable } from "mobx";

 class TodoStore {



    constructor() {
        makeAutoObservable(this)

        this.activeTask = null;

        this.todos = {
             0: {"id": 0, "title": "qwertyfdsqfsdfdef", "description": "Asdfghfsadfdsafdsaf", "completed": false},
             1: {"id": 1, "title": "qwerty", "description": "Asdfgh", "completed": false},
             2: {"id": 2, "title": "qwerty", "description": "Asdfgh", "completed": false},
             3: {"id": 3, "title": "qwerty", "description": "Asdfgh", "completed": false},
        };
    }

    addTodo(array) {
        this.todos[Object.keys(this.todos).length] = array;
    }

    setTodo(todo) {
        this.activeTask = todo ;

    }

    removeTodo(id) {
       delete this.todos[id];
    }

