import "./sortingStyle.css";
import todoListState from "../../store/todoListState";

const SortingTodo = () => {
    const { todos } = todoListState;

    const sorting = (arr) => {
        return arr.sort(function (a, b) {
            if (a.title > b.title) {
                return 1;
            }
            if (a.title < b.title) {
                return -1;
            }

            return 0;
        });
    };

    return <button onClick={() => console.log(sorting(todos))}>A-Z</button>;
};

export default SortingTodo;
