import React from "react"
import './listStyle.css'
import { observer } from "mobx-react-lite"
import todoListState from "../../store/todoListState"

export const CreateList = observer(() => {
    return (
        <div>
            {todoListState.todos.map((todo) =>
                <div className="todoList" key={todo.id}>
                    <p>{'#' + todo.id}</p>
                    <p>{todo.title}</p>
                    <p>{(todo.description.length >= 15) ? todo.description : todo.description.slice(0, 14) + "..."}</p>
                    <input type="checkbox" checked={todo.completed} onChange={() => todoListState.completeTodo(todo.id)}/>
                    {/* <button onClick={() => todoListState.removeTodo(todo.id)}>X</button> */}
                </div>
            )}
        </div>
    )
})
