import React from "react";
import './style.css'
import { observer } from "mobx-react-lite";
import { store } from "../../store";

export const TodoList = observer((activeTodo) => {

    const { todos } = store;
    const {id} = activeTodo

    const [complete, setComplete] = React.useState("");

    React.useEffect(() => {
        completeFunc();
    }, [todos[id - 1]?.completed]);

    const completeFunc = () => {
        if (todos[id - 1]?.completed) {
            setComplete(true);
        } else {
            setComplete(false);
        }
    };

    {console.log(activeTodo)}

    return (
        <ul className="todoListBlock">
            {Object.values(todos).map((todo) => (
                <li
                    className="todo"
                    key={todo.id}
                    onClick={() => store.setTodo(todo)}
                >
                    {console.log(todo.completed)}
                    <p className="id">#{todo.id}</p>
                    <p className="title">
                        {todo.title?.length >= 7
                            ? todo.title.slice(0, 7) + "..."
                            : todo.title}
                    </p>
                    <p className="description">
                        {todo.description?.length >= 7
                            ? todo.description.slice(0, 7) + "..."
                            : todo.description}
                    </p>
                    <div className="inputBtnBlock">
                        <input
                            type="checkbox"
                            checked={complete}
                            onChange={() => store.completeTodo(todo.id)}
                        />
                        <button
                            onClick={() => (
                                store.removeTodo(todo.id - 1)
                            )}
                        >
                            x
                        </button>
                    </div>
                </li>
            ))}
        </ul>
    );
});
